import logo from './logo.svg';
import './App.css';
import { Link } from 'react-router-dom';
import Title from './Title.js';
// Import the functions you need from the SDKs you need
import { initializeApp } from 'https://www.gstatic.com/firebasejs/9.14.0/firebase-app.js';
import { getAuth, signInWithPopup, signOut, GoogleAuthProvider, onAuthStateChanged } from 'https://www.gstatic.com/firebasejs/9.14.0/firebase-auth.js';
import { getFirestore, addDoc, collection, query, where, getDocs, Timestamp } from 'https://www.gstatic.com/firebasejs/9.14.0/firebase-firestore.js';
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyDSJwKWKgzd8W49qVYVvjY3kvGHpciWyYU",
  authDomain: "bear-blog.firebaseapp.com",
  projectId: "bear-blog",
  storageBucket: "bear-blog.appspot.com",
  messagingSenderId: "266223683560",
  appId: "1:266223683560:web:3a30436c38aa3df2b5865d"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const provider = new GoogleAuthProvider();

// When the sign-in button is clicked, the pop-up window will appear for users to sign in via Google.
signInBtn.onclick = () => signInWithPopup(auth, provider);

// When the sign-out button is clicked, the user is signed out and the page reloads.
//signOutBtn.onclick = () => {
    //signOut(auth, provider);
    //location.reload()
//}

function App() {
  return (
    <div className="App">
      <Title />

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Bear Blog
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>

      <div className = "button">
          <Link to="/">
            <button className = "app_bottomButtonleft">
              Home 
            </button>
          </Link>

          <Link to="/profile/:profileId">
            <button className = "app_bottomButtonright">
              Profile 
            </button>
          </Link>
      </div>
    </div>
  );
}

export default App;
