import React from 'react'
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { getAuth, signInWithPopup, signOut, GoogleAuthProvider, onAuthStateChanged } from 'https://www.gstatic.com/firebasejs/9.14.0/firebase-auth.js';
import { initializeApp } from 'https://www.gstatic.com/firebasejs/9.14.0/firebase-app.js';
import "./Signin.css";

const firebaseConfig = {
    apiKey: "AIzaSyDSJwKWKgzd8W49qVYVvjY3kvGHpciWyYU",
    authDomain: "bear-blog.firebaseapp.com",
    projectId: "bear-blog",
    storageBucket: "bear-blog.appspot.com",
    messagingSenderId: "266223683560",
    appId: "1:266223683560:web:3a30436c38aa3df2b5865d"
  };

const app = initializeApp(firebaseConfig);
const provider = new GoogleAuthProvider();

const auth = getAuth(app);

function Signin() {
    const [signedIn, changeSignedInState] = useState(false)
    const [currentUser, setUser] = useState(false)
    function signInPopUp() {
        signInWithPopup(auth, provider);
    }

    function signOutCool() {
        signOut(auth, provider);
    }
    onAuthStateChanged(auth, (user) => {
        // user argument is null if a user signs out.
        console.log("auth state changed")
        if (user) {
            console.log("hello you are signed in")
            changeSignedInState(true)
            setUser(user)
            // let signInBtn = document.getElementById("signInBtn");
            // signInBtn.hidden = true;
            // let welcome = document.createElement('h1')
            // welcome.innerHTML = '<h1>Hello ${user.displayName}!</h1>'
        } else {
            console.log("you are signed out")
            // let signInBtn = document.getElementById("signInBtn")
            // signInBtn.hidden = false;
            changeSignedInState(false)
        }
    });

    return (
        <div className="container">
            {!signedIn && <div className="signInPrep">
                <h1 className="signInHeader">Please Sign In To Make Posts</h1>
                <button id="signInBtn" className="btn-primary" onClick={signInPopUp}>Sign in with Google</button>
            </div>}
            {signedIn && <div classname="afterSignedIn">
                <h1 className="welcomeMessage">Hello, {currentUser.displayName}</h1>
                <Link to="/">
                    <button className="homeButton">
                        See Homepage
                    </button>
                </Link>
                <button id="signOutBtn" className="btn-primary" onClick={signOutCool}>Sign Out</button>
            </div>}
        </div>
    );
}





export default Signin;
