import React from 'react'
import {TextField} from "@material-ui/core";
import { useState } from 'react';
import {Routes, Route, useNavigate} from 'react-router-dom';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';



function MakePost(props) {
    const {signInPopUp, signOutCool, currentUser, addPostToDB} = props;
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleChange = (event) => {
        setMessage(event.target.value);
      };

    const navigateHome = () => {
        navigate('/');
    };

    function submitPost() {
        addPostToDB(message)
    }

    return (
        <div>
            {!currentUser && <div className="notSignedIn">
                <h1>Please sign in if you want to make a post to Bear Blog!</h1>
            </div>}
            {currentUser && <div className="signedIn">
                <h1>Get started with your post!</h1>
                <TextField 
                fullWidth 
                multiline={true} 
                variant="outlined"
                value={message}
                onChange={handleChange}/>
                {message != '' && <div>
                    <Popup trigger={<button className="submitButton" onClick={submitPost}>
                        Post!
                    </button>} position="bottom center">
                        <div>
                            Your post has been successfully made!
                        </div>
                        <button className="returnHome" onClick={navigateHome}>
                            Return Home
                        </button>
                    </Popup>
                </div>}
            </div>}
        </div>
    );
}

export default MakePost;
