import React from 'react'
import {TextField} from "@material-ui/core";
import { useState } from 'react';
import {Routes, Route, useNavigate} from 'react-router-dom';



function MakePost(props) {
    const {signInPopUp, signOutCool, currentUser, addPostToDB, items} = props;
    const [message, setMessage] = useState('');
    const [isOpen, setIsOpen] = useState(false)
    const navigate = useNavigate();

    const handleChange = (event) => {
        setMessage(event.target.value);
      };

    const navigateHome = () => {
        navigate('/');
    };

    function submitPost() {
        addPostToDB(message)
        setIsOpen(true)
    }

    return (
        <div>
            {!currentUser && <div className="notSignedIn">
                <h1>Please sign in if you want to make a post to Bear Blog!</h1>
            </div>}
            {currentUser && <div className="signedIn">
                <h1>Get started with your post!</h1>
                <TextField 
                fullWidth 
                multiline={true} 
                variant="outlined"
                value={message}
                onChange={handleChange}/>
                {message != '' && <div>
                    <button className="submitButton" onClick={submitPost}>
                        Post!
                    </button>
                    {isOpen && <div className="successfulPost">
                        <h2>Your post has been successfully created!</h2>
                        <button onClick={navigateHome}>Return Home</button>
                    </div>}
                </div>}
            </div>}
        </div>
    );
}

export default MakePost;
